import {GET_ADMIN, ADMIN_ERROR,GET_USERS, USERS_ERROR} from './types'
import axios from 'axios';



export const ADD_RECIPE ='ADD_RECIPE'
export const REMOVE_FROM_CALENDAR = 'REMOVE_FROM_CALENDAR'

export const addRecipe = ({ day, recipe, meal }) => ({
	 type: ADD_RECIPE,
    recipe,
    day,
    meal,
})

export const getUsers = () => async dispatch => {

    try{
        const res = await axios.get('https://countriesnow.space/api/v0.1/countries/capital')
        dispatch( {
            type: GET_USERS,
            payload: res.data.data
        })
    }
    catch(e){
        dispatch( {
            type: USERS_ERROR,
            payload: console.log(e),
        })
    }

}



export const removeFromCalendar = ({ day, meal }) => ({
	type: REMOVE_FROM_CALENDAR,
    day,
    meal,
})
